public class TicTacToeRules {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Tic Tac Toe Rules");
		
		System.out.println("Tic Tac Toe is a game for 2 players");
		
		System.out.println("1. A grid of 9 squares is drawn");
		
		System.out.println("2. The first player draws an X in an emoty square");
		
		System.out.println("3. The second player draws an O in an empty square");
		
		System.out.println("4. Steps 2 and 3 are repeated until a Tic Tac pattern is created: patterns can be diagonal, horizontal, or vertical");
		
		System.out.println("5. A winning pattern is completed by drawing a line trhough the pattern");

	}

}
